import { getAddress } from "ethers/src.ts/address";
import { toBeHex } from "ethers/src.ts/utils/maths";

import { Typed } from "ethers/src.ts/abi/typed";
import { Coder } from "ethers/src.ts/abi/coders/abstract-coder";

import type { Reader, Writer } from "ethers/src.ts/abi/coders/abstract-coder";


/**
 *  @_ignore
 */
export class AddressCoder extends Coder {

    constructor(localName: string) {
        super("address", "address", localName, false);
    }

    defaultValue(): string {
        return "0x0000000000000000000000000000000000000000";
    }

    encode(writer: Writer, _value: string | Typed): number {
        let value = Typed.dereference(_value, "string");
        try {
            value = getAddress(value);
        } catch (error: any) {
            return this._throwError(error.message, _value);
        }
        return writer.writeValue(value);
    }

    decode(reader: Reader): any {
        return getAddress(toBeHex(reader.readValue(), 20));
    }
}
