/**
 *  A fundamental building block of Ethereum is the underlying
 *  cryptographic primitives.
 *
 *  @_section: api/crypto:Cryptographic Functions   [about-crypto]
 */

null

// We import all these so we can export lock()
import { computeHmac } from "ethers/src.ts/crypto/hmac";
import { keccak256 } from "ethers/src.ts/crypto/keccak";
import { ripemd160 } from "ethers/src.ts/crypto/ripemd160";
import { pbkdf2 } from "ethers/src.ts/crypto/pbkdf2";
import { randomBytes } from "ethers/src.ts/crypto/random";
import { scrypt, scryptSync } from "ethers/src.ts/crypto/scrypt";
import { sha256, sha512 } from "ethers/src.ts/crypto/sha2";

export {
    computeHmac,

    randomBytes,

    keccak256,
    ripemd160,
    sha256, sha512,

    pbkdf2,
    scrypt, scryptSync
};

export { SigningKey } from "ethers/src.ts/crypto/signing-key";
export { Signature } from "ethers/src.ts/crypto/signature";

/**
 *  Once called, prevents any future change to the underlying cryptographic
 *  primitives using the ``.register`` feature for hooks.
 */
function lock(): void {
    computeHmac.lock();
    keccak256.lock();
    pbkdf2.lock();
    randomBytes.lock();
    ripemd160.lock();
    scrypt.lock();
    scryptSync.lock();
    sha256.lock();
    sha512.lock();
    randomBytes.lock();
}

export { lock };

